SELECT apell FROM empleado WHERE SALARIO > 1000 AND OFICIO = 'VENDEDOR';
SELECT DISTINCT OFICIO FROM EMPLEADO ORDER BY OFICIO;
SELECT APELL APELLIDO, LENGTH(APELL)LONGITUD, INSTR(APELL,'A')POSICION FROM EMPLEADO WHERE LENGTH(APELL)>5;
SELECT RPAD(APELL,10,'*')APELLIDO FROM EMPLEADO WHERE COMISION IS NOT NULL;
SELECT DEPNUME DEPARTAMENTO, SUM(SALARIO) SUMA_SALARIOS FROM EMPLEADO GROUP BY DEPNUME ORDER BY DEPNUME;
SELECT COUNT(NUMEMP)EMPLEADOS FROM EMPLEADO WHERE OFICIO='VENDEDOR';
SELECT DEPNUME DEPARTAMENTO, AVG(SALARIO)SALARIO_MEDIO FROM EMPLEADO WHERE COMISION IS NOT NULL GROUP BY DEPNUME;
SELECT DEPNUME, MAX(SALARIO)MAXIMO FROM EMPLEADO GROUP BY DEPNUME HAVING MAX(SALARIO)>2900 ORDER BY DEPNUME;
SELECT OFICIO, COUNT(OFICIO)FROM EMPLEADO GROUP BY OFICIO ORDER BY COUNT(OFICIO)DESC;
SELECT APELL APELLIDOS, NOMBREDEP DEPARTAMENTO FROM EMPLEADO E, DEPARTAMENTO D WHERE E.DEPNUME=D.NUMEDEP ORDER BY D.NOMBREDEP;
SELECT APELL APELLIDOS, NOMBREDEP DEPARTAMENTO FROM EMPLEADO E, DEPARTAMENTO D WHERE E.DEPNUME(+)=D.NUMEDEP ORDER BY D.NOMBREDEP;
SELECT APELL APELLIDOS, OFICIO, SALARIO FROM EMPLEADO WHERE SALARIO=(SELECT MIN(SALARIO)FROM EMPLEADO);
SELECT D.NOMBREDEP FROM EMPLEADO E, DEPARTAMENTO D WHERE D.NUMEDEP=E.DEPNUME GROUP BY D.NOMBREDEP HAVING MIN(SALARIO)>(SELECT MIN(SALARIO)FROM EMPLEADO WHERE DEPNUME=20);
SELECT APELL APELLIDOS, OFICIO, DEPNUME FROM EMPLEADO WHERE OFICIO=(SELECT OFICIO FROM EMPLEADO WHERE APELL='NADAL');
SELECT APELL APELLIDOS, OFICIO, DEPNUME FROM EMPLEADO WHERE DEPNUME IN(SELECT NUMEDEP FROM DEPARTAMENTO WHERE LOCALDEP IN('GIJON','AVILES'));
SELECT APELL APELLIDOS, OFICIO, SALARIO, DEPNUME FROM EMPLEADO WHERE OFICIO=(SELECT OFICIO FROM EMPLEADO WHERE APELL='GIL')AND SALARIO =(SELECT SALARIO FROM EMPLEADO WHERE APELL='GIL');
SELECT APELL APELLIDO, OFICIO, DEPNUME, SALARIO FROM EMPLEADO WHERE OFICIO=(SELECT OFICIO FROM EMPLEADO WHERE APELL='MARTIN')AND SALARIO BETWEEN 1000 AND 2000;